import React, { useState, useEffect } from "react";
import { Navbar, NavTabs, SearchBar } from "../components";
import Pagination from "@material-ui/lab/Pagination";
import axios from "axios";
import myPathRutgersData from "../util/myPathRutgersData";
import { Close } from "@material-ui/icons";
import {
  Typography,
  makeStyles,
  CircularProgress,
  Button,
  CardActions,
  Card,
  CardContent,
  CardActionArea,
  List,
  ListItem,
  ListItemAvatar,
  ListItemText,
  Avatar,
  Dialog,
  DialogContent,
  IconButton,
} from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  appBar: {
    backgroundColor: "#78935d",
    color: "white",
  },
  title: {
    flexGrow: 1,
    textAlign: "center",
    fontWeight: 600,
  },
  paperTitle: {
    flexGrow: 1,
    textAlign: "center",
    position: "absolute",
    width: "100%",
    marginTop: "9%",
    color: "#78935d",
    fontSize: "3.25vw",
    fontWeight: "semi-bold",
  },
  paperSubTitle: {
    flexGrow: 1,
    textAlign: "center",
    position: "absolute",
    width: "100%",
    marginTop: "15%",
    color: "#707066",
    fontSize: "1.12vw",
  },
  plantImage: {
    width: "100%",
  },
  loading: {
    marginLeft: "50%",
    marginTop: "20px",
    color: "#78935d",
  },
  cardImage: {
    maxWidth: 500,
    textAlign: "center",
    boxShadow: "0 6px 10px rgba(0,0,0,.08), 0 0 6px rgba(0,0,0,.05)",
    transition:
      ".3s transform cubic-bezier(.155,1.105,.295,1.12),.3s box-shadow,.3s -webkit-transform cubic-bezier(.155,1.105,.295,1.12)",
    margin: "10px",
    "&:hover": {
      transform: "scale(1.03)",
      boxShadow: "0 10px 20px rgba(0,0,0,.12), 0 4px 8px rgba(0,0,0,.06)",
    },
  },
  listIcons: {
    display: "flex",
  },
  card: {
    display: "inline-block",
    margin: "20px",
  },
  thumbnail: {
    height: "350px",
    width: "100%",
  },
  dialogThumbnail: {
    height: "80%",
    width: "100%",
  },
  plantCard: {
    display: "inline-block",
    marginTop: "20px",
  },
  learnMoreBtn: {
    color: "#78935d",
    fontWeight: 500,
  },
  pagination: {
    margin: "10px",
  },
  dialogTitle: {
    display: "inline-block",
    margin: "20px",
  },
  closeBtn: {
    float: "right",
    margin: "10px",
  },
  icons: {
    color: "#78935d",
  },
  familyName: {
    marginTop: "15px",
    color: "grey",
    fontStyle: "italic",
    fontSize: "16px",
  },
}));

export default function Tab1() {
  const classes = useStyles();
  const [loadingData, setLoadingData] = useState(false);
  const [searchValue, setSearchValue] = useState("");
  const [open, setOpen] = useState(false);
  const itemsPerPage = 9;
  const [page, setPage] = useState(1);
  // const [myPathInfo, setmyPathInfo] = useState([]);
  const [myPathApp, setMyPathApp] = useState(null);

  // useEffect(() => {
  //   const fetchData = async () => {
  //     setLoadingData(true);
  //     try {
  //       const { data } = await axios.get(
  //         "https://data.sfgov.org/resource/vmnk-skih.json?$select=*"
  //       );
  //       setLoadingData(false);
  //       setmyPathInfo(data);
  //     } catch (err) {}
  //   };
  //   fetchData();
  // }, []);

  const filterNames = ({ common_name }) => {
    return (
      common_name &&
      common_name.toLowerCase().indexOf(searchValue.toLowerCase()) !== -1
    );
  };

  const handleClickOpen = (event, item) => {
    setOpen(true);
    setMyPathApp(item);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div>
      <div className={classes.root}>
      <Navbar />
        <SearchBar onSearch={setSearchValue} value={searchValue} />
        {loadingData === true ? (
          <CircularProgress className={classes.loading} />
        ) : (
          <>
            {myPathRutgersData &&
              myPathRutgersData
                .filter(filterNames)
                .slice((page - 1) * itemsPerPage, page * itemsPerPage)
                .map((apps) => {

                  return (
                    <span
                      key={apps.common_name}
                      className={classes.plantCard}
                    >
                      <Card className={classes.cardImage}>
                        <CardActionArea>
                          <img
                            alt="plantImage"
                            key={apps.common_name}
                            src={
                              myPathRutgersData[apps.common_name]
                                ? myPathRutgersData[apps.common_name].image
                                : "https://i.imgur.com/VRaN8uw.jpg"
                            }
                            className={classes.thumbnail}
                          />
                          <CardContent>
                            <Typography
                              gutterBottom
                              variant="h5"
                              component="h2"
                            >
                              {apps.common_name}
                            </Typography>
                            <Typography
                              variant="body2"
                              color="textSecondary"
                              component="p"
                            >
                              <List className={classes.listIcons}>
                                <ListItem key={apps.name}>
                                  <ListItemAvatar>
                                    <Avatar></Avatar>
                                  </ListItemAvatar>
                                  <ListItemText
                                    primary="Type"
                                    secondary={apps.name}
                                  />
                                </ListItem>
                                <ListItem key={apps.name}>
                                  <ListItemAvatar>
                                    <Avatar></Avatar>
                                  </ListItemAvatar>
                                  <ListItemText
                                    primary="Color"
                                    secondary={
                                      typeof apps.flower_color !== "undefined"
                                        ? apps.flower_color.replace(
                                            /;/g,
                                            ", "
                                          )
                                        : "Not Available"
                                    }
                                  />
                                </ListItem>
                                <ListItem key={apps.water_needs}>
                                  <ListItemAvatar>
                                    <Avatar></Avatar>
                                  </ListItemAvatar>
                                  <ListItemText
                                    primary="Water Needs"
                                    secondary={apps.name}
                                  />
                                </ListItem>
                              </List>
                              <CardActions>
                                <Button
                                  size="small"
                                  onClick={(event) =>
                                    handleClickOpen(event, apps)
                                  }
                                  className={classes.learnMoreBtn}
                                >
                                  Learn More
                                </Button>
                              </CardActions>
                            </Typography>
                          </CardContent>
                        </CardActionArea>
                      </Card>
                    </span>
                  );
                })}
            <div>
              {/* <Pagination
                count={Math.ceil(myPathInfo.length / itemsPerPage)}
                page={page}
                onChange={(e, value) => setPage(value)}
                defaultPage={1}
                size="medium"
                showFirstButton
                showLastButton
                className={classes.pagination}
              /> */}
            </div>

            {open && myPathApp && (
              <Dialog
                key={myPathApp}
                onClose={handleClose}
                aria-labelledby="customized-dialog-title"
                open={open}
              >
                <div>
                  <Typography className={classes.dialogTitle} variant="h6">
                    {myPathApp.common_name}
                  </Typography>
                  {handleClose ? (
                    <IconButton
                      className={classes.closeBtn}
                      aria-label="close"
                      onClick={handleClose}
                    >
                      <Close />
                    </IconButton>
                  ) : null}
                </div>
                <DialogContent dividers>
                  <img
                    alt="myPathImg"
                    key={myPathApp.common_name}
                    src={
                      myPathRutgersData[myPathApp.common_name]
                        ? myPathRutgersData[myPathApp.common_name].image
                        : "https://i.imgur.com/VRaN8uw.jpg"
                    }
                    className={classes.dialogThumbnail}
                  />
                  <Typography gutterBottom className={classes.familyName}>
                    Family Name: {myPathApp.family_name}
                  </Typography>
                  <Typography
                    variant="body2"
                    color="textSecondary"
                    component="p"
                  >
                    <List>
                      <ListItem key={myPathApp.flower_color}>
                        <ListItemAvatar></ListItemAvatar>
                        <ListItemText
                          primary="Flower Color"
                          secondary={
                            typeof myPathApp.flower_color !== "undefined"
                              ? myPathApp.flower_color.replace(/;/g, ", ")
                              : "Not Available"
                          }
                        />
                      </ListItem>
                    </List>
                  </Typography>
                </DialogContent>
              </Dialog>
            )}
          </>
        )}
      </div>
      <NavTabs />
    </div>
  );
}
