import React, { useState, useEffect } from "react";
import { Navbar, NavTabs, SearchBar } from "../components";
import Pagination from "@material-ui/lab/Pagination";
import axios from "axios";
import applicationImages from "../util/applicationImages";
import { Close } from "@material-ui/icons";
import {
  AppBar,
  Toolbar,
  Typography,
  makeStyles,
  CircularProgress,
  Button,
  CardActions,
  Card,
  CardContent,
  CardActionArea,
  List,
  ListItem,
  ListItemAvatar,
  ListItemText,
  Avatar,
  Dialog,
  DialogContent,
  IconButton,
} from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  appBar: {
    backgroundColor: "#78935d",
    color: "white",
  },
  title: {
    flexGrow: 1,
    textAlign: "center",
    fontWeight: 600,
  },
  paperTitle: {
    flexGrow: 1,
    textAlign: "center",
    position: "absolute",
    width: "100%",
    marginTop: "9%",
    color: "#78935d",
    fontSize: "3.25vw",
    fontWeight: "semi-bold",
  },
  paperSubTitle: {
    flexGrow: 1,
    textAlign: "center",
    position: "absolute",
    width: "100%",
    marginTop: "15%",
    color: "#707066",
    fontSize: "1.12vw",
  },
  applicationImage: {
    width: "100%",
  },
  loading: {
    marginLeft: "50%",
    marginTop: "20px",
    color: "#78935d",
  },
  cardImage: {
    maxWidth: 500,
    textAlign: "center",
    boxShadow: "0 6px 10px rgba(0,0,0,.08), 0 0 6px rgba(0,0,0,.05)",
    transition:
      ".3s transform cubic-bezier(.155,1.105,.295,1.12),.3s box-shadow,.3s -webkit-transform cubic-bezier(.155,1.105,.295,1.12)",
    margin: "10px",
    "&:hover": {
      transform: "scale(1.03)",
      boxShadow: "0 10px 20px rgba(0,0,0,.12), 0 4px 8px rgba(0,0,0,.06)",
    },
  },
  listIcons: {
    display: "flex",
  },
  card: {
    display: "inline-block",
    margin: "20px",
  },
  thumbnail: {
    height: "350px",
    width: "100%",
  },
  dialogThumbnail: {
    height: "80%",
    width: "100%",
  },
  applicationCard: {
    display: "inline-block",
    marginTop: "20px",
  },
  learnMoreBtn: {
    color: "#78935d",
    fontWeight: 500,
  },
  pagination: {
    margin: "10px",
  },
  dialogTitle: {
    display: "inline-block",
    margin: "20px",
  },
  closeBtn: {
    float: "right",
    margin: "10px",
  },
  icons: {
    color: "#78935d",
  },
  familyName: {
    marginTop: "15px",
    color: "grey",
    fontStyle: "italic",
    fontSize: "16px",
  },
}));

export default function Tab1() {
  const classes = useStyles();
  const [loadingData, setLoadingData] = useState(false);
  const [searchValue, setSearchValue] = useState("");
  const [open, setOpen] = useState(false);
  const itemsPerPage = 9;
  const [page, setPage] = useState(1);
  const [myPathInfo, setmyPathInfo] = useState([]);
  const [application, setapplication] = useState(null);

  useEffect(() => {
    const fetchapplicationData = async () => {
      setLoadingData(true);
      try {
        const { data } = await axios.get(
          "https://data.sfgov.org/resource/vmnk-skih.json?$select=*"
        );
        setLoadingData(false);
        setmyPathInfo(data);
      } catch (err) {}
    };
    fetchapplicationData();
  }, []);

  const filterNames = ({ common_name }) => {
    return (
      common_name &&
      common_name.toLowerCase().indexOf(searchValue.toLowerCase()) !== -1
    );
  };

  const handleClickOpen = (event, item) => {
    setOpen(true);
    setapplication(item);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div>
      <div className={classes.root}>
      <Navbar />
        <SearchBar onSearch={setSearchValue} value={searchValue} />
        {loadingData === true ? (
          <CircularProgress className={classes.loading} />
        ) : (
          <>
            {myPathInfo &&
              myPathInfo
                .filter(filterNames)
                .slice((page - 1) * itemsPerPage, page * itemsPerPage)
                .map((applications) => {
                  return (
                    <span
                      key={applications.common_name}
                      className={classes.applicationCard}
                    >
                      <Card className={classes.cardImage}>
                        <CardActionArea>
                          <img
                            alt="applicationImage"
                            key={applications.common_name}
                            src={
                              applicationImages[applications.common_name]
                                ? applicationImages[applications.common_name].image
                                : "https://i.imgur.com/VRaN8uw.jpg"
                            }
                            className={classes.thumbnail}
                          />
                          <CardContent>
                            <Typography
                              gutterBottom
                              variant="h5"
                              component="h2"
                            >
                              {applications.common_name}
                            </Typography>
                            <Typography
                              variant="body2"
                              color="textSecondary"
                              component="p"
                            >
                              <List className={classes.listIcons}>
                                <ListItem key={applications.application_type}>
                                  <ListItemAvatar>
                                    <Avatar></Avatar>
                                  </ListItemAvatar>
                                  <ListItemText
                                    primary="Type"
                                    secondary={applications.application_type}
                                  />
                                </ListItem>
                                <ListItem key={applications.flower_color}>
                                  <ListItemAvatar>
                                    <Avatar></Avatar>
                                  </ListItemAvatar>
                                  <ListItemText
                                    primary="Color"
                                    secondary={
                                      typeof applications.flower_color !== "undefined"
                                        ? applications.flower_color.replace(
                                            /;/g,
                                            ", "
                                          )
                                        : "Not Available"
                                    }
                                  />
                                </ListItem>
                                <ListItem key={applications.water_needs}>
                                  <ListItemAvatar>
                                    <Avatar></Avatar>
                                  </ListItemAvatar>
                                  <ListItemText
                                    primary="Water Needs"
                                    secondary={applications.water_needs}
                                  />
                                </ListItem>
                              </List>
                              <CardActions>
                                <Button
                                  size="small"
                                  onClick={(event) =>
                                    handleClickOpen(event, applications)
                                  }
                                  className={classes.learnMoreBtn}
                                >
                                  Learn More
                                </Button>
                              </CardActions>
                            </Typography>
                          </CardContent>
                        </CardActionArea>
                      </Card>
                    </span>
                  );
                })}
            <div>
              <Pagination
                count={Math.ceil(myPathInfo.length / itemsPerPage)}
                page={page}
                onChange={(e, value) => setPage(value)}
                defaultPage={1}
                size="medium"
                showFirstButton
                showLastButton
                className={classes.pagination}
              />
            </div>

            {open && application && (
              <Dialog
                key={application}
                onClose={handleClose}
                aria-labelledby="customized-dialog-title"
                open={open}
              >
                <div>
                  <Typography className={classes.dialogTitle} variant="h6">
                    {application.common_name}
                  </Typography>
                  {handleClose ? (
                    <IconButton
                      className={classes.closeBtn}
                      aria-label="close"
                      onClick={handleClose}
                    >
                      <Close />
                    </IconButton>
                  ) : null}
                </div>
                <DialogContent dividers>
                  <img
                    alt="applicationImage"
                    key={application.common_name}
                    src={
                      applicationImages[application.common_name]
                        ? applicationImages[application.common_name].image
                        : "https://i.imgur.com/VRaN8uw.jpg"
                    }
                    className={classes.dialogThumbnail}
                  />
                  <Typography gutterBottom className={classes.familyName}>
                    Family Name: {application.family_name}
                  </Typography>
                  <Typography
                    variant="body2"
                    color="textSecondary"
                    component="p"
                  >
                    <List>
                      <ListItem key={application.application_type}>
                        <ListItemAvatar></ListItemAvatar>
                        <ListItemText
                          primary="Type"
                          secondary={application.application_type}
                        />
                      </ListItem>
                      <ListItem key={application.flower_color}>
                        <ListItemAvatar></ListItemAvatar>
                        <ListItemText
                          primary="Flower Color"
                          secondary={
                            typeof application.flower_color !== "undefined"
                              ? application.flower_color.replace(/;/g, ", ")
                              : "Not Available"
                          }
                        />
                      </ListItem>
                      <ListItem key={application.associated_wildlife}>
                        <ListItemAvatar></ListItemAvatar>
                        <ListItemText
                          primary="Associated Wildlife"
                          secondary={
                            typeof application.associated_wildlife !== "undefined"
                              ? application.associated_wildlife.replace(/;/g, ", ")
                              : "Not Available"
                          }
                        />
                      </ListItem>
                      <ListItem key={application.bloom_time}>
                        <ListItemAvatar></ListItemAvatar>
                        <ListItemText
                          primary="Bloom Time"
                          secondary={
                            typeof application.boom_time !== "undefined"
                              ? application.boom_time.replace(/;/g, ", ")
                              : "Not Available"
                          }
                        />
                      </ListItem>
                    </List>
                  </Typography>
                </DialogContent>
              </Dialog>
            )}
          </>
        )}
      </div>
      <NavTabs />
    </div>
  );
}
