import React, { useState, useEffect } from "react";
import { Navbar, NavTabs } from "../components";
import { SearchBar } from "./components/";
import Pagination from "@material-ui/lab/Pagination";
import axios from "axios";
import PlantImages from "./util/plantImage";
import { Close } from "@material-ui/icons";
import {
  AppBar,
  Toolbar,
  Typography,
  makeStyles,
  CircularProgress,
  Button,
  CardActions,
  Card,
  CardContent,
  CardActionArea,
  List,
  ListItem,
  ListItemAvatar,
  ListItemText,
  Avatar,
  Dialog,
  DialogContent,
  IconButton,
} from "@material-ui/core";
const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  appBar: {
    backgroundColor: "#78935d",
    color: "white",
  },
  title: {
    flexGrow: 1,
    textAlign: "center",
    fontWeight: 600,
  },
  paperTitle: {
    flexGrow: 1,
    textAlign: "center",
    position: "absolute",
    width: "100%",
    marginTop: "9%",
    color: "#78935d",
    fontSize: "3.25vw",
    fontWeight: "semi-bold",
  },
  paperSubTitle: {
    flexGrow: 1,
    textAlign: "center",
    position: "absolute",
    width: "100%",
    marginTop: "15%",
    color: "#707066",
    fontSize: "1.12vw",
  },
  plantImage: {
    width: "100%",
  },
  loading: {
    marginLeft: "50%",
    marginTop: "20px",
    color: "#78935d",
  },
  cardImage: {
    maxWidth: 500,
    textAlign: "center",
    boxShadow: "0 6px 10px rgba(0,0,0,.08), 0 0 6px rgba(0,0,0,.05)",
    transition:
      ".3s transform cubic-bezier(.155,1.105,.295,1.12),.3s box-shadow,.3s -webkit-transform cubic-bezier(.155,1.105,.295,1.12)",
    margin: "10px",
    "&:hover": {
      transform: "scale(1.03)",
      boxShadow: "0 10px 20px rgba(0,0,0,.12), 0 4px 8px rgba(0,0,0,.06)",
    },
  },
  listIcons: {
    display: "flex",
  },
  card: {
    display: "inline-block",
    margin: "20px",
  },
  thumbnail: {
    height: "350px",
    width: "100%",
  },
  dialogThumbnail: {
    height: "80%",
    width: "100%",
  },
  plantCard: {
    display: "inline-block",
    marginTop: "20px",
  },
  learnMoreBtn: {
    color: "#78935d",
    fontWeight: 500,
  },
  pagination: {
    margin: "10px",
  },
  dialogTitle: {
    display: "inline-block",
    margin: "20px",
  },
  closeBtn: {
    float: "right",
    margin: "10px",
  },
  icons: {
    color: "#78935d",
  },
  familyName: {
    marginTop: "15px",
    color: "grey",
    fontStyle: "italic",
    fontSize: "16px",
  },
}));

export default function Tab1() {
  const classes = useStyles();
  const [loadingData, setLoadingData] = useState(false);
  const [searchValue, setSearchValue] = useState("");
  const [open, setOpen] = useState(false);
  const itemsPerPage = 9;
  const [page, setPage] = useState(1);
  const [plantsInfo, setPlantsInfo] = useState([]);
  const [plant, setPlant] = useState(null);

  // useEffect(() => {
  //   const fetchPlantData = async () => {
  //     setLoadingData(true);
  //     try {
  //       const { data } = await axios.get(
  //         "https://data.sfgov.org/resource/vmnk-skih.json?$select=*"
  //       );
  //       setLoadingData(false);
  //       setPlantsInfo(data);
  //     } catch (err) {}
  //   };
  //   fetchPlantData();
  // }, []);

  const filterNames = ({ common_name }) => {
    return (
      common_name &&
      common_name.toLowerCase().indexOf(searchValue.toLowerCase()) !== -1
    );
  };

  const handleClickOpen = (event, item) => {
    setOpen(true);
    setPlant(item);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div>
      <Navbar />
      <div className={classes.root}>
        <AppBar className={classes.appBar} position="static">
          <Toolbar>
            <Typography variant="h6" className={classes.title}>
              San Francisco Plant Finder
            </Typography>
          </Toolbar>
        </AppBar>
        <Typography className={classes.paperTitle}>Plant Finder</Typography>
        <Typography className={classes.paperSubTitle}>
          A resource for gardeners, designers, ecologists <br /> and anyone who
          is interested in greening neighborhoods, <br /> enhancing our urban
          ecology and surviving the drought.
        </Typography>
        <SearchBar onSearch={setSearchValue} value={searchValue} />

        {loadingData === true ? (
          <CircularProgress className={classes.loading} />
        ) : (
          <>
            {plantsInfo &&
              plantsInfo
                .filter(filterNames)
                .slice((page - 1) * itemsPerPage, page * itemsPerPage)
                .map((plants) => {
                  return (
                    <span
                      key={plants.common_name}
                      className={classes.plantCard}
                    >
                      <Card className={classes.cardImage}>
                        <CardActionArea>
                          <img
                            alt="plantImage"
                            key={plants.common_name}
                            src={
                              PlantImages[plants.common_name]
                                ? PlantImages[plants.common_name].image
                                : "https://i.imgur.com/VRaN8uw.jpg"
                            }
                            className={classes.thumbnail}
                          />
                          <CardContent>
                            <Typography
                              gutterBottom
                              variant="h5"
                              component="h2"
                            >
                              {plants.common_name}
                            </Typography>
                            <Typography
                              variant="body2"
                              color="textSecondary"
                              component="p"
                            >
                              <List className={classes.listIcons}>
                                <ListItem key={plants.plant_type}>
                                  <ListItemAvatar>
                                    <Avatar></Avatar>
                                  </ListItemAvatar>
                                  <ListItemText
                                    primary="Type"
                                    secondary={plants.plant_type}
                                  />
                                </ListItem>
                                <ListItem key={plants.flower_color}>
                                  <ListItemAvatar>
                                    <Avatar></Avatar>
                                  </ListItemAvatar>
                                  <ListItemText
                                    primary="Color"
                                    secondary={
                                      typeof plants.flower_color !== "undefined"
                                        ? plants.flower_color.replace(
                                            /;/g,
                                            ", "
                                          )
                                        : "Not Available"
                                    }
                                  />
                                </ListItem>
                                <ListItem key={plants.water_needs}>
                                  <ListItemAvatar>
                                    <Avatar></Avatar>
                                  </ListItemAvatar>
                                  <ListItemText
                                    primary="Water Needs"
                                    secondary={plants.water_needs}
                                  />
                                </ListItem>
                              </List>
                              <CardActions>
                                <Button
                                  size="small"
                                  onClick={(event) =>
                                    handleClickOpen(event, plants)
                                  }
                                  className={classes.learnMoreBtn}
                                >
                                  Learn More
                                </Button>
                              </CardActions>
                            </Typography>
                          </CardContent>
                        </CardActionArea>
                      </Card>
                    </span>
                  );
                })}
            <div>
              <Pagination
                count={Math.ceil(plantsInfo.length / itemsPerPage)}
                page={page}
                onChange={(e, value) => setPage(value)}
                defaultPage={1}
                size="medium"
                showFirstButton
                showLastButton
                className={classes.pagination}
              />
            </div>

            {open && plant && (
              <Dialog
                key={plant}
                onClose={handleClose}
                aria-labelledby="customized-dialog-title"
                open={open}
              >
                <div>
                  <Typography className={classes.dialogTitle} variant="h6">
                    {plant.common_name}
                  </Typography>
                  {handleClose ? (
                    <IconButton
                      className={classes.closeBtn}
                      aria-label="close"
                      onClick={handleClose}
                    >
                      <Close />
                    </IconButton>
                  ) : null}
                </div>
                <DialogContent dividers>
                  <img
                    alt="plantImage"
                    key={plant.common_name}
                    src={
                      PlantImages[plant.common_name]
                        ? PlantImages[plant.common_name].image
                        : "https://i.imgur.com/VRaN8uw.jpg"
                    }
                    className={classes.dialogThumbnail}
                  />
                  <Typography gutterBottom className={classes.familyName}>
                    Family Name: {plant.family_name}
                  </Typography>
                  <Typography
                    variant="body2"
                    color="textSecondary"
                    component="p"
                  >
                    <List>
                      <ListItem key={plant.plant_type}>
                        <ListItemAvatar></ListItemAvatar>
                        <ListItemText
                          primary="Type"
                          secondary={plant.plant_type}
                        />
                      </ListItem>
                      <ListItem key={plant.flower_color}>
                        <ListItemAvatar></ListItemAvatar>
                        <ListItemText
                          primary="Flower Color"
                          secondary={
                            typeof plant.flower_color !== "undefined"
                              ? plant.flower_color.replace(/;/g, ", ")
                              : "Not Available"
                          }
                        />
                      </ListItem>
                      <ListItem key={plant.associated_wildlife}>
                        <ListItemAvatar></ListItemAvatar>
                        <ListItemText
                          primary="Associated Wildlife"
                          secondary={
                            typeof plant.associated_wildlife !== "undefined"
                              ? plant.associated_wildlife.replace(/;/g, ", ")
                              : "Not Available"
                          }
                        />
                      </ListItem>
                      <ListItem key={plant.bloom_time}>
                        <ListItemAvatar></ListItemAvatar>
                        <ListItemText
                          primary="Bloom Time"
                          secondary={
                            typeof plant.boom_time !== "undefined"
                              ? plant.boom_time.replace(/;/g, ", ")
                              : "Not Available"
                          }
                        />
                      </ListItem>
                    </List>
                  </Typography>
                </DialogContent>
              </Dialog>
            )}
          </>
        )}
      </div>
      <NavTabs />
    </div>
  );
}
