import React, {Component} from 'react';
import {Link, useHistory} from 'react-router-dom';
import BottomNavigation from '@material-ui/core/BottomNavigation';
import BottomNavigationAction from '@material-ui/core/BottomNavigationAction';
// import LanguageIcon from '@material-ui/icons/Language';
// import GroupIcon from '@material-ui/icons/Group';
// import ShoppingBasketIcon from '@material-ui/icons/ShoppingBasket';
// import HelpIcon from '@material-ui/icons/Help';
// import EmailIcon from '@material-ui/icons/Email';
// import './PrimaryNav.css';

class NavTabs extends Component {
  state = {
    value: 0,
    pathMap: [
      '/tab1',
      '/tab2',
      '/tab3',
    ]
  };

  componentWillReceiveProps(newProps) {
    const {pathname} = newProps.location;
    const {pathMap} = this.state;
    const history = useHistory();
    const value = pathMap.indexOf(pathname);

    if (value > -1) {
      this.setState({
        value
      });
    }
  }

  handleChange = (event, value) => {
    this.setState({ value });
  };

  render() {
    const {value, pathMap} = this.state;

    return (
      <BottomNavigation
        value={value}
        onChange={this.handleChange}
        showLabels
        className="nav primary"
      >
        <BottomNavigationAction label="Panoramas" component={Link} to={pathMap[0]} />
        <BottomNavigationAction label="Members" component={Link} to={pathMap[1]} />
        <BottomNavigationAction label="Shop" component={Link} to={pathMap[2]} />
        <BottomNavigationAction label="About" component={Link} to={pathMap[3]} />
        <BottomNavigationAction label="Subscribe" component={Link} to={pathMap[4]} />
      </BottomNavigation>
    );
  }
}

export default NavTabs();
